import "../css/About.css"




function About(){


    return(
        <div className='container'>        

        <h1 style={{color: 'blue', textAlign: 'center'}}>Welcome to our thrift shop! </h1>
        <p className='p'>We are a community-driven store that offers a wide variety of gently used clothing, accessories.</p>              


        <p className='p'>We believe that everyone should have access to stylish and high-quality clothing and we are committed to making that happen.</p> 
         <p className='p'>So come visit us and see what treasures you can find!</p>
        </div>



    )
}

export default About;
