//component to add products to json
//Gloria

import React, {useState} from 'react'
import "../css/AddProduct.css"





function AddProduct({ addNewProduct }){


    const [title , setTitle] = useState('')
    const [price , setPrice] = useState('')
    const [image, setImage] = useState('')    
    const [description , setDescription] = useState('')
    const [category , setCategory] = useState('')
    const [rating , setRating] = useState('')




     const handleSubmit = (e) =>{
        e.preventDefault()

        const newProduct = {

            title: title,
            price: price,
            description: description,
            category: category,
            image: image,           
            rating: rating,

        }

        fetch('http://localhost:8002/products', {

        method:'POST',

        headers: {
            'Content-Type' : 'application/json'
        },

        body:JSON.stringify(newProduct),
     })

     .then((resp)=> resp.json())
     .then((product)=> addNewProduct(product))

    }

    return(
        <div>
                <div className = 'ui segment' onSubmit = {handleSubmit}>
                <form onSubmit = {handleSubmit}>
        <div className="form-group">

            <label>Title</label>
            <input type="text" 
            name ='title' value = {title} className="form-control" placeholder="Enter Product Name" onChange ={(e) => setTitle(e.target.value)}/>

        </div>

        <div className="form-group">
            <label>Price</label>
            <input type="number" name ='price' value = {price} className="form-control" placeholder="Enter Price in Shillings" onChange ={(e) => setPrice(e.target.value)}/>
        </div>

        <div className="form-group">
            <label>Description</label>
            <input type="text" name ='description' value = {description} className="form-control" placeholder="Enter Product Description" onChange ={(e) => setDescription(e.target.value)}/>
        </div>

        <div className="form-group">
            <label>Category</label>
            <input type="text" name ='category' value = {category} className="form-control" placeholder="Enter Product Category" onChange ={(e) => setCategory(e.target.value)}/>
        </div>

        <div className="form-group">
            <label>Image</label>
            <input type="text" name ='image' value = {image} className="form-control" placeholder="Enter Image Link" onChange ={(e) => setImage(e.target.value)}/>
        </div>

        <div className="form-group">
            <label>Rating</label>
            <input type="number" name ='rating' value = {rating} className="form-control" placeholder="Enter Rating" step = '0.1' onChange ={(e) => setRating(e.target.value)}/>
        </div>


        <button type="submit" className="btn btn-primary">Submit</button>
        </form>

                </div>
            </div>

    )
    }
    export default AddProduct;
