import React from "react";
import "../css/ProductCard.css"

function AddToCart({productsInCart}){
    const quantity=1;  

    const displayProductsInCart = productsInCart.map((product) => {
        return(
            (<div className="col-sm-3"> 
            <div className="card" style={{width: "18rem"}}>
                <img className="card-img-top " src={product.image} alt={product.title} />
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text">{product.description}</p>
                </div>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item">Price: Ksh {product.price}</li>
                  <li className="list-group-item">Section: {product.category}</li>
                  <li className="list-group-item">Rating: {product.rating}/5</li>
                </ul>
                <div className="card-body">
                {(quantity===0) 
                ? (<button className='ui mini orange button' id="btn">+ Add To Cart</button>) 
                : (<div className='d-flex align-items-centre justify-content-centre' style={{gap: "0.5rem"}}> 
                    <button id="btn2" className='ui mini blue button'>-</button>
                    <div>
                      <span className='fs-5'>{quantity}</span> in cart
                    </div>
                    <button className='ui mini blue button'>+</button>

                    <div className='d-flex align-items-centre flex-column' style={{gap: "0.5rem"}}>

                    </div>

                </div>)}
                <button className= "ui mini red button">Remove</button>

                </div>
                </div>
    </div>)
        )
    })
    return (
    <>
    {displayProductsInCart}
    </>
    )
}

export default AddToCart;
